import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

// Base styles for the component
const containerStyle = {
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  justifyContent: "center",
  height: "100%",
  textAlign: "center",
};

const profileCardStyle = {
  backgroundColor: "#f0f0f0",
  padding: "20px",
  maxWidth: "600px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  marginBottom: "20px",
};

const skillsStyle = {
  backgroundColor: "#333",
  color: "white",
  borderRadius: "5px",
  padding: "5px 10px",
  margin: "5px",
};

function CandidateList() {
  const [searchText, setSearchText] = useState("");
  const [filteredCandidates, setFilteredCandidates] = useState([]);
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    const storedCandidates = localStorage.getItem("candidates");
    if (storedCandidates) {
      const parsedCandidates = JSON.parse(storedCandidates);
      setCandidates(parsedCandidates);
      setFilteredCandidates(parsedCandidates);
    }
  }, []);

  const handleSearch = () => {
    const filtered = candidates.filter((candidate) =>
      candidate.skills.some((skill) =>
        skill.toLowerCase().includes(searchText.toLowerCase())
      )
    );
    setFilteredCandidates(filtered);
  };

  const handleListAll = () => {
    setFilteredCandidates(candidates);
    setSearchText("");
  };

  const profilesFoundTitle =
    filteredCandidates.length === 0
      ? "No candidates found."
      : `${filteredCandidates.length} profiles found`;

  return (
    <div style={containerStyle}>
      <nav>
        <h1>Job Portal</h1>
        <div>
          <Link to="/candidate/registration">Candidate Registration</Link>
          <Link to="/">Home</Link>
        </div>
      </nav>
      <div>
        <input
          type="text"
          placeholder="Search skills"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={handleListAll}>All</button>
      </div>
      <p>{profilesFoundTitle}</p>
      <div>
        {filteredCandidates.map((candidate, index) => (
          <div key={index} style={profileCardStyle}>
            <h2>Role: {candidate.role}</h2>
            <p>Name: {candidate.name}</p>
            <p>Email: {candidate.email}</p>
            <div>
              <p>Skills</p>
              <div>
                {candidate.skills.map((skill, skillIndex) => (
                  <span key={skillIndex} style={skillsStyle}>
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CandidateList;
